mapper.insert:
INSERT INTO MEM_INFO (
	MEM_EMAIL
	, MEM_PASS
	, MEM_NAME
	, MEM_AGE
	, MEM_BIRTH
	, MEM_GENDER
	, MEM_TEL1
	, MEM_ADDR
) VALUES (
	#{memEmail}
	, #{memPass}
	, #{memName}
	, #{memAge}
	, #{memBirth}
	, #{memGender}
	, #{memTel1}
	, #{memAddr}
)

mapper.select:
SELECT MEM_EMAIL
FROM MEM_INFO
WHERE MEM_EMAIL = #{memEmail}

mapper.insert:
INSERT INTO COMPANY_INFO (
	COM_NUM
	, COM_PASS
	, COM_EMAIL
	, COM_NAME
	, COM_CEO
	, COM_LOC
	, COM_TYPE
	, COM_TEL
) VALUES (
	#{comNum}
	, #{comPass}
	, #{comEmail}
	, #{comName}
	, #{comCeo}
	, #{comLoc}
	, #{comType}
	, #{comTel}
)

mapper.update:
UPDATE	MEM_INFO
SET SESSION_ID = #{setId}
WHERE MEM_EMAIL = #{memEmail}

UPDATE MEM_INFO 
SET MEM_PASS = #{memPass}
    ,MEM_NAME = #{memName}
    ,MEM_GENDER = #{memGender}
    ,MEM_BIRTH = #{memBirth}
    ,MEM_TEL1 = #{memTel1}
    ,MEM_TEL2 = #{memTel2}
    ,MEM_ADDR = #{memAddr}
    ,MEM_MERRY = #{memMerry}
WHERE MEM_EMAIL = #{memEmail}

UPDATE COMPANY_INFO
SET COM_SESSION_ID = #{setId}
WHERE COM_NUM = #{comNum}

UPDATE RECRUIT_LIST
SET RECRUIT_VIEWS = (SELECT (MAX(RECRUIT_VIEWS) +1) FROM RECRUIT_LIST)
WHERE ANNOUNCE_NUM = #{announceNum}

mapper.select:
SELECT ANNOUNCE_NUM 
FROM RECRUIT_LIST
WHERE UPTO_HIREDATE < #{toDay}

mapper.insert:
INSERT INTO COM_MYPAGE(
    COM_MYPAGE_NUM
    ,COM_NUM
    ,ANNOUNCE_NUM
    ,RESUME_NUM
) VALUES(
    (SELECT NVL(MAX(COM_MYPAGE_NUM)+1,1) FROM COM_MYPAGE)
    ,#{comNum}
    ,#{announceNum}
    ,#{resumeNum}
)
