mapper.update:
UPDATE MEM_INFO
SET MEM_NAME = #{memName}
	, MEM_PASS = #{memPass}
	, MEM_TEL1 = #{memTel1}
	, MEM_TEL2 = #{memTel2}
	, MEM_ADDR = #{memAddr}
	
	, MEM_IMAGE = #{memImage}
	
WHERE MEM_EMAIL = #{memEmail}

UPDATE MEM_RESUME
SET
RESUME_NAME = #{resumeName}							
, EDU_GRADE = #{eduGrade}
, EDU_SCHOOL = #{eduSchool}
, EDU_LOC	= #{eduLoc}
, EDU_TYPE = #{eduType}
, EDU_MAJOR = #{eduMajor}	
, EDU_BEGIN_DATE = #{eduBeginDate}
, EDU_END_DATE = #{eduEndDate}
, EDU_SCORE = #{eduScore}
, EDU_IS_OVER = #{eduIsOver}
, HOPE_TYPE = #{hopeType}
, HOPE_SAL = #{hopeSal}
, HOPE_LOC = #{hopeLoc}
, HOPE_TIME = #{hopeTime}
WHERE RESUME_NUM = #{resumeNum}

mapper.insert:
MERGE INTO LICENSE
USING DUAL
ON (LIC_NUM = #{licNum})
WHEN MATCHED THEN
	UPDATE
	SET
	LIC_NAME = #{licName}
	, LIC_LOC = #{licLoc}
	, LIC_GRADE = #{licGrade}
	, LIC_DATE = #{licDate}
WHEN NOT MATCHED THEN
	INSERT (
		LIC_NUM
		, LIC_NAME
		, LIC_LOC
		, LIC_GRADE
		, LIC_DATE
		, RESUME_NUM
	) VALUES (
		#{licNum}
	 	, #{licName}
	 	, #{licLoc}
	 	, #{licGrade}
	 	, #{licDate}
	 	, #{resumeNum}
	)

MERGE INTO PROFILES
USING DUAL
ON (PRO_NUM = #{proNum})
WHEN MATCHED THEN
	UPDATE
	SET
	PRO_TITLE = #{proTitle}
	, PRO_CONTENT = #{proContent}
WHEN NOT MATCHED THEN
	INSERT (
		PRO_NUM
		, PRO_TITLE
		, PRO_CONTENT
		, RESUME_NUM
	) VALUES (
		#{proNum}
	 	, #{proTitle}
	 	, #{proContent}
	 	, #{resumeNum}
	)

MERGE INTO CAREER
USING DUAL
ON (CAR_NUM = #{carNum})
WHEN MATCHED THEN
	UPDATE
	SET
	CAR_COMPANY = #{carCompany}	
	, CAR_CAREER = #{carCareer}	
	, CAR_TYPE = #{carType}	
	, CAR_POSITION = #{carPosition}
WHEN NOT MATCHED THEN
	INSERT (
		CAR_NUM
		, CAR_COMPANY
		, CAR_CAREER
		, CAR_TYPE
		, CAR_POSITION
		, RESUME_NUM
	) VALUES (
		#{carNum}
	 	, #{carCompany}
	 	, #{carCareer}
	 	, #{carType}
	 	, #{carPosition}
	 	, #{resumeNum}
	)

INSERT INTO LIKE_RECRUIT (
	LIKE_RECRUIT_NUM
	, ANNOUNCE_NUM
	, MEM_EMAIL
) VALUES(
	(SELECT NVL(MAX(LIKE_RECRUIT_NUM) + 1, 1) FROM LIKE_RECRUIT)
	, #{announceNum}	
	, #{memEmail}	
)

mapper.delete:
DELETE LIKE_RECRUIT
WHERE MEM_EMAIL = #{memEmail}
AND ANNOUNCE_NUM = #{announceNum}

mapper.select:
		SELECT * FROM LIKE_COMPANY
        WHERE ANNOUNCE_NUM = #{announceNum}
        AND MEM_EMAIL = #{memEmail}

mapper.insert:
INSERT INTO LIKE_(
	LIKE_NUM
	, COM_NUM
	, MEM_EMAIL)VALUES(
	(SELECT NVL(MAX(LIKE_NUM) + 1, 1) FROM LIKE_COMPANY)
	, #{comNum}
	, #{memEmail}
	)

mapper.delete:
DELETE LIKE_COMPANY
WHERE COM_NUM = #{comNum} 
AND MEM_EMAIL = #{memEmail}
