INSERT INTO MEM_RESUME (
	RESUME_NUM
	, RESUME_NAME
	, EDU_GRADE
	, EDU_SCHOOL
	, EDU_LOC
	, EDU_TYPE
	, EDU_MAJOR
	, EDU_BEGIN_DATE
	, EDU_END_DATE
	, EDU_IS_OVER
	, EDU_SCORE
	, BE_COMPANY
	, BE_CAREER
	, BE_TYPE
	, BE_POSITION
	, HOPE_TYPE
	, HOPE_SAL
	, HOPE_LOC
	, HOPE_TIME
	, MEM_EMAIL
) VALUES (
	(SELECT NVL(MAX(RESUME_NUM) + 1 , 1) FROM MEM_RESUME)
	, #{resumeName}
	, #{eduGrade}
	, #{eduSchool}
	, #{eduLoc}
	, #{eduType}
	, #{eduMajor}
	, #{eduBeginDate}
	, #{eduEndDate}
	, #{eduIsOver}
	, #{eduScore}
	, #{beCompany}
	, #{beCareer}
	, #{beType}
	, #{bePosition}
	, #{hopeType}
	, #{hopeSal}
	, #{hopeLoc}
	, #{hopeTime}
	, #{memEmail}
)

INSERT ALL

	INTO LICENSE (
		LIC_NUM
		, LIC_NAME
		, LIC_LOC
		, LIC_GRADE
		, LIC_DATE
		, RESUME_NUM
	) VALUES (
		(SELECT NVL(MAX(LIC_NUM)+1,1) + #{index} FROM LICENSE)
	 	, #{license.licName}
	 	, #{license.licLoc}
	 	, #{license.licGrade}
	 	, #{license.licDate}
	 	, (SELECT NVL(MAX(RESUME_NUM), 1) FROM MEM_RESUME)
	)

